import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginForm.css';

const ForgotPassword = () => {
  const navigate = useNavigate();
  const [step, setStep] = useState('email');
  const [email, setEmail] = useState('');
  const [code, setCode] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);

  const handleEmailSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      console.log('Sending request with email:', email); // Debug log

      const response = await fetch('http://localhost:8000/api/forgot-password/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        },
        body: JSON.stringify({ email })
      });

      console.log('Response status:', response.status); // Debug log
      
      const data = await response.json();
      console.log('Response data:', data); // Debug log

      if (!response.ok && response.status !== 200) {
        throw new Error(data.error || 'Failed to send verification code');
      }

      if (data.error && data.error.includes('During development')) {
        // Show the verification code in development
        setSuccess(data.error);
      } else {
        setSuccess(data.message || 'Verification code sent to your email');
      }
      
      setStep('code');
      
    } catch (err) {
      console.error('Full error:', err); // Debug log
      setError(err.message || 'Something went wrong');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2 className="login-title">Reset Password</h2>
        {error && (
          <div className="error-message">
            {error}
            {process.env.NODE_ENV === 'development' && (
              <div className="error-details">
                Check the browser console and Django server logs for more details.
              </div>
            )}
          </div>
        )}
        {success && <div className="success-message">{success}</div>}

        {step === 'email' && (
          <form onSubmit={handleEmailSubmit} className="login-form">
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="login-input"
              required
            />
            <button 
              type="submit" 
              className="login-button"
              disabled={loading}
            >
              {loading ? 'Sending...' : 'Send Code'}
            </button>
          </form>
        )}

        <button 
          onClick={() => navigate('/login')}
          className="back-button mt-4"
          disabled={loading}
        >
          Back to Login
        </button>
      </div>
    </div>
  );
};

export default ForgotPassword;
