import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const MenuPage = () => {
 const navigate = useNavigate();
 const [user, setUser] = useState(null);
 const [isAdmin, setIsAdmin] = useState(false);

 const menuItems = [
   {
     id: 1,
     name: 'Basic Plan',
     price: '$10/month',
     features: ['10GB Storage', 'Basic Support', 'Limited Access'],
     color: 'bg-blue-500'
   },
   {
     id: 2, 
     name: 'Pro Plan',
     price: '$20/month',
     features: ['50GB Storage', '24/7 Support', 'Full Access'],
     color: 'bg-purple-500'
   },
   {
     id: 3,
     name: 'Enterprise Plan', 
     price: '$50/month',
     features: ['Unlimited Storage', 'Priority Support', 'Custom Solutions'],
     color: 'bg-green-500'
   }
 ];

 useEffect(() => {
   fetch('http://localhost:8000/api/user/', {
     headers: {
       'Authorization': `Bearer ${localStorage.getItem('access')}`
     }
   })
   .then(res => res.json())
   .then(data => {
     setUser(data);
     setIsAdmin(data.is_staff);
   });
 }, []);

 const handleLogout = () => {
   localStorage.removeItem('access');
   navigate('/');
 };

 return (
   <div className="min-h-screen bg-gray-100 flex flex-col items-center py-12 px-4">
     <div className="max-w-4xl w-full bg-white rounded-lg shadow-lg p-6 mb-8">
       <div className="flex justify-between items-center">
         <h1 className="text-3xl font-bold text-gray-900">Welcome, {user?.username}</h1>
         <div className="space-x-4">
           {isAdmin && (
             <button
               onClick={() => navigate('/admin')}
               className="px-4 py-2 bg-indigo-600 hover:bg-indigo-700 text-white font-medium rounded-lg transition-colors"
             >
               Admin Dashboard
             </button>
           )}
           <button
             onClick={handleLogout}
             className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white font-medium rounded-lg transition-colors"
           >
             Logout
           </button>
         </div>
       </div>
     </div>

     <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-4xl w-full">
       {menuItems.map(item => (
         <div key={item.id} className="bg-white rounded-xl shadow-lg overflow-hidden transform hover:scale-105 transition-transform duration-200">
           <div className={`${item.color} h-2`} />
           <div className="p-6">
             <h2 className="text-2xl font-bold text-gray-900 mb-2">{item.name}</h2>
             <p className="text-3xl font-bold text-gray-800 mb-4">{item.price}</p>
             <ul className="space-y-2 mb-6">
               {item.features.map((feature, index) => (
                 <li key={index} className="flex items-center text-gray-600">
                   <span className="text-green-500 mr-2">âœ“</span>
                   {feature}
                 </li>
               ))}
             </ul>
             <button 
               className={`w-full py-2 ${item.color} hover:opacity-90 text-white font-bold rounded-lg transition-opacity`}
             >
               Purchase
             </button>
           </div>
         </div>
       ))}
     </div>
   </div>
 );
};

export default MenuPage;
