import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ClientsTable.css';

const ClientsTable = () => {
  const navigate = useNavigate();
  const [clients, setClients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [newClient, setNewClient] = useState({
    name: '',
    email: '',
    client_id: ''
  });

  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/clients/', {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('access')}`
        }
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Failed to fetch clients');
      setClients(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const validateForm = () => {
    if (!newClient.name.trim()) return 'Name is required';
    if (!newClient.email.trim()) return 'Email is required';
    if (!newClient.client_id.trim()) return 'Client ID is required';
    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(newClient.email)) return 'Invalid email format';
    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationError = validateForm();
    if (validationError) {
      setError(validationError);
      return;
    }

    setError('');
    setSubmitting(true);

    try {
      const response = await fetch('http://localhost:8000/api/clients/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('access')}`
        },
        body: JSON.stringify(newClient)
      });

      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Failed to add client');

      setClients([data, ...clients]);
      setShowForm(false);
      setNewClient({ name: '', email: '', client_id: '' });
      setSuccess('Client added successfully!');
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      setError(err.message);
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return (
    <div className="clients-container">
      <div className="loading-spinner">Loading...</div>
    </div>
  );

  return (
    <div className="clients-container">
      <button 
        onClick={() => navigate('/menu')}
        className="back-button"
      >
        Back to Menu
      </button>

      <div className="clients-header">
        <h2>Clients List</h2>
        <button 
          className="add-client-button"
          onClick={() => setShowForm(true)}
        >
          Add New Client
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}
      {success && <div className="success-message">{success}</div>}

      {showForm && (
        <div className="client-form">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label>Name:</label>
              <input
                type="text"
                value={newClient.name}
                onChange={(e) => setNewClient({...newClient, name: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Email:</label>
              <input
                type="email"
                value={newClient.email}
                onChange={(e) => setNewClient({...newClient, email: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Client ID:</label>
              <input
                type="text"
                value={newClient.client_id}
                onChange={(e) => setNewClient({...newClient, client_id: e.target.value})}
                required
              />
            </div>
            <div className="form-buttons">
              <button 
                type="submit" 
                disabled={submitting}
              >
                {submitting ? 'Adding...' : 'Add Client'}
              </button>
              <button 
                type="button" 
                onClick={() => {
                  setShowForm(false);
                  setError('');
                }}
                disabled={submitting}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      <table className="clients-table">
        <thead>
          <tr>
            <th>Client ID</th>
            <th>Name</th>
            <th>Email</th>
            <th>Created By</th>
            <th>Created At</th>
          </tr>
        </thead>
        <tbody>
          {clients.map(client => (
            <tr key={client.id}>
              <td>{client.client_id}</td>
              <td>{client.name}</td>
              <td>{client.email}</td>
              <td>{client.created_by}</td>
              <td>{new Date(client.created_at).toLocaleDateString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ClientsTable;
