import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './MenuPage.css';

const MenuPage = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);
  const [isAdmin, setIsAdmin] = useState(false);
  
  const menuItems = [
    {
      id: 1,
      name: 'Basic Plan',
      price: '$10/month',
      features: ['10GB Storage', 'Basic Support', 'Limited Access']
    },
    {
      id: 2,
      name: 'Pro Plan',
      price: '$20/month',
      features: ['50GB Storage', '24/7 Support', 'Full Access']
    },
    {
      id: 3,
      name: 'Enterprise Plan',
      price: '$50/month',
      features: ['Unlimited Storage', 'Priority Support', 'Custom Solutions']
    }
  ];

  useEffect(() => {
    fetch('http://localhost:8000/api/user/', {
      headers: {
        'Authorization': `Bearer ${localStorage.getItem('access')}`
      }
    })
    .then(res => res.json())
    .then(data => {
      setUser(data);
      setIsAdmin(data.is_staff);
    });
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('access');
    navigate('/');
  };

  return (
    <div className="menu-container">
      <div className="header">
        <h1>Welcome, {user?.username}</h1>
        <div className="header-buttons">
          {isAdmin && (
            <button 
              className="admin-button"
              onClick={() => navigate('/admin')}
            >
              Admin Dashboard
            </button>
          )}
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      <div className="plans-container">
        {menuItems.map(item => (
          <div key={item.id} className="plan-card">
            <h2>{item.name}</h2>
            <div className="price">{item.price}</div>
            <ul className="features-list">
              {item.features.map((feature, index) => (
                <li key={index}>
                  <span className="checkmark">âœ“</span>
                  {feature}
                </li>
              ))}
            </ul>
            <button className="purchase-button">Purchase</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MenuPage;
