import React from 'react';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import RegisterForm from './components/RegisterForm';
import MenuPage from './components/MenuPage';
import AdminDashboard from './components/AdminDashboard';
import UserProfile from './components/UserProfile';
import ForgotPassword from './components/ForgotPassword';
import AboutPage from './components/AboutPage';

function App() {
  const isAuthenticated = () => !!localStorage.getItem('access');
  const isAdmin = () => {
    // Simple check - you might want to decode JWT instead
    return !!localStorage.getItem('access');
  };

  return (
    <div className="App">
      <BrowserRouter>
        <Navbar />
        <div className="page-content">
          <Routes>
            <Route path="/" element={isAuthenticated() ? <Navigate to="/menu" /> : <LoginForm />} />
            <Route path="/login" element={isAuthenticated() ? <Navigate to="/menu" /> : <LoginForm />} />
            <Route path="/register" element={<RegisterForm />} />
            <Route path="/forgot-password" element={<ForgotPassword />} />
            <Route path="/about" element={<AboutPage />} />

            <Route 
              path="/menu" 
              element={isAuthenticated() ? <MenuPage /> : <Navigate to="/login" />} 
            />

            <Route 
              path="/admin" 
              element={isAdmin() ? <AdminDashboard /> : <Navigate to="/menu" />} 
            />

            <Route 
              path="/profile" 
              element={isAuthenticated() ? <UserProfile /> : <Navigate to="/login" />} 
            />
          </Routes>
        </div>
      </BrowserRouter>
    </div>
  );
}

export default App;
